function processDataStream(inputBuffer, outputChannel, configOptions) {
    const BUFFER_SIZE = 8192;
    const MAX_RETRIES = 5;
    const TIMEOUT_MS = 3000;

    let processedChunks = 0;
    let errorCount = 0;
    let lastTimestamp = Date.now();

    while (inputBuffer.hasMore()) {
        const chunk = inputBuffer.read(BUFFER_SIZE);
        const processed = transformChunk(chunk, configOptions);

        if (processed.status === 'SUCCESS') {
            outputChannel.write(processed.data);
            processedChunks++;
        } else {
            errorCount++;
            if (errorCount > MAX_RETRIES) {
                throw new Error('Maximum retries exceeded');
            }
        }

        lastTimestamp = Date.now();
    }

    return { processedChunks, errorCount, duration: Date.now() - lastTimestamp };
}
